import { createContext, useState } from "react";


export const CartContext = createContext ();

const CartContextProvider = ({children}) => {
    const [cartList, setCartList] = useState([]);

    const addToCart = (productos, qty) => {
        const found = cartList.find(el=> el.id == productos.id);

        const findDuplicated = (found, cartList) => {
            cartList.array.forEach(element => {
                if(found.id === element.id){
                    return element.cantidad = qty + element.cantidad
                }
                
            });
        }
        if (found) {findDuplicated(found, cartList)} else {setCartList([...cartList, productos])} 
    }

    return (
        <>
        <CartContext.Provider value={{cartList, addToCart}}> 
            {children}
        </CartContext.Provider>
        </>
    )
}


export default CartContextProvider;
