import { createContext, useState } from "react";


export const CartContext = createContext ();

const CartContextProvider = ({children}) => {
    const [cartList, setCartList] = useState([]);

    const addToCart = (productos, qty) => { // funcion para agregar productos 
        const found = cartList.find(el=> el.id == productos.id);

        const findDuplicated = (found, cartList) => {
            cartList.array.forEach(element => {
                if(found.id === element.id){
                    return element.cantidad = qty + element.cantidad
                }
                
            });
        }
        if (found) {findDuplicated(found, cartList)} else {setCartList([...cartList, productos])} 
    }

    const removeItem= (id) => { // funcion para remover un solo producto
        const result = cartList.filter(el => el.id !== parseInt(id));
        setCartList(result)
    }

    const clear = () =>{ // funcion para vaciar el carrito
        setCartList ([])
    } 

    const isInCart = (productos) => { // funcion para saber si el producto a agregar ya se encuentra en el carrito
        const found = cartList.find(el => el.id == productos.id)

    }


    return (
        <>
        <CartContext.Provider value={{data}}> 
            {children}
        </CartContext.Provider>
        </>
    )

    // se pone en data lo que se quiere compartir con los componentes
    const data = {addToCart, cartList, clear, removeItem}
}


export default CartContextProvider;
