import {useState} from "react";
import Button from 'react-bootstrap/Button';

const ItemCount = ({ stock, addCarrito, onAdd }) => {

    const [contador, setContador] = useState(0);

    const increment = () => {
      if (contador < stock) {

        setContador(contador + 1);
      }
    };

    const decrement = () => {
      if (contador > 0) {

        setContador(contador - 1);

      }
    };

    // const agregar = () => {
    //   addCarrito={contador};
    //   alert(`se agregaron ${contador} productos al carrito`);
    //   console.log(agregar);
    // };

    return (

      <div>

        <div className="text-center">

          <Button variant="dark"  onClick={increment} >+</Button>

          <span>{contador}</span>

          <Button variant="dark"  onClick={decrement}>-</Button>
          <Button variant="dark" onClick={() => onAdd(contador)} style={{margin:'mt-5' , width:'100%'}}>Agregar al carrito</Button>

        </div>




      </div>

    );


  };

  export default ItemCount;
