import { useContext } from "react";
import { CartContext } from "./CartContext";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import {Link} from 'react-router-dom';

const Cart = () => {
    const test = useContext(CartContext);

    return (
        <>
            <div>
                <h2 style={{textAlign:'center'}}>Tus productos seleccionados</h2>
            
                    <Link to='/' style={{textAlign:'center'}} ><Button variant="dark">Continuar comprando</Button></Link>
                    {
                        (test.cartList.length > 0)
                        ? <Button variant="danger" onClick={test.removeList}>Borrar productos</Button>
                        : <p style={{textAlign:'center'}}>TU CARRITO DE COMPRAS ESTA VACIO</p>
                    }

           
                    {
                        test.cartList.length > 0 ? 
                        test.cartList.map(producto=> 
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Img variant="top" src={producto.imgItem} />
                                <Card.Title>{producto.nameItem}</Card.Title>
                                <Card.Text>{producto.descriptionItem}</Card.Text>
                                <Card.Text>{producto.costItem}</Card.Text>
                                <Button onClick={() => test.deleteItem(producto.id)}>Borrar producto</Button>
                            </Card.Body>
                        </Card>
                        )
                        :<h1></h1>
        
                    }
                </div>
                

        </>        
    )   
}    

export default Cart;
